package pruebasIunit.ayrh.app;

public class Calculadora {
	
	public int sumar(int n1, int n2) {
		// TODO Auto-generated method stub
		return (n1+n2);
	}
	
	public int restar(int n1, int n2) {
		// TODO Auto-generated method stub
		return (n1-n2);
	}
	public double division(double d1, double d2) {
		return (d1/d2);
	}
}
